mapper.update:
UPDATE contents 
SET title=#{title}, content=#{content}, web=#{web}, word=#{word}
WHERE contentsno=#{contentsno}

UPDATE contents
SET file1=#{file1}, thumb1=#{thumb1}, size1=#{size1}
WHERE contentsno=#{contentsno}

mapper.select:
SELECT COUNT(*) as passwd_cnt
FROM contents
WHERE contentsno=#{contentsno} AND passwd=#{passwd}

mapper.update:
UPDATE contents
SET youtube=#{youtube}
WHERE contentsno=#{contentsno} AND passwd=#{passwd}

mapper.select:
SELECT COUNT(*) as cnt FROM contents
WHERE genreno=#{genreno}
WHERE genreno=#{genreno} AND (title LIKE '%' || #{word} || '%' OR content LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')

SELECT contentsno, genreno, title, content, recom, cnt, replycnt, rdate, word, file1, thumb1, size1, r FROM ( SELECT contentsno, genreno, title, content, recom, cnt, replycnt, rdate, word, file1, thumb1, size1, rownum as r FROM ( SELECT contentsno, genreno, title, content, recom, cnt, replycnt, rdate, word, file1, thumb1, size1 FROM contents
WHERE genreno=#{genreno}
WHERE genreno=#{genreno} AND (title LIKE '%' || #{word} || '%' OR content LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')
ORDER BY contentsno DESC ) ) WHERE r >= #{startNum} AND r <= #{endNum}

mapper.update:
UPDATE contents
SET ansnum = ansnum + 1
WHERE grpno = #{grpno} AND ansnum > #{ansnum}
